#define SHIFT   (1<<0)
#define ALT_GR  (1<<1)
#define CONTROL (1<<2)
#define ALT     (1<<3)
#define SHIFT_L (1<<4)
#define SHIFT_R (1<<5)
#define CTRL_L  (1<<6)
#define CTRL_R  (1<<7)


#define KEY_BUFFER_SIZE_EXP 4
static unsigned status=0; //idle
static unsigned modifier=0;
static char     keyBuffer[1<<KEY_BUFFER_SIZE_EXP];
static unsigned keyPutI=0;
static unsigned keyGetI=0;

static void putChar(char ch)
{
 keyBuffer[keyPutI++]=ch;
 keyPutI&=((1<<KEY_BUFFER_SIZE_EXP)-1);
}


static void onRelease(unsigned c0,unsigned c1)
{
 switch(c1)
 {
  case 0xf7:
   modifier&=~(1<<c0);
  break;
 }//end switch
}

static void onPress(unsigned c0,unsigned c1)
{
 switch(c1)
 {
  case 0xf7:
   modifier|=(1<<c0);
  break;

  case 0xf6: //string
   {
    const char* s=func_buf[c0];
    while(1)
    {
     if (*s==0) break;
     putChar(*s++);
    }
   }
  break;

  default:
   putChar(c0);
  break;
 }//end switch
}

static void onKeycode(unsigned kc)
{
 unsigned code=(modifier<key_maps_len)?key_maps[modifier][kc&0x7f]
                                      :0;
 unsigned c0=0xff&(code>>0);
 unsigned c1=0xff&(code>>8);
 if (kc&0x80) onRelease(c0,c1);
    else      onPress(c0,c1);
}

static void onScancode(unsigned char sc)
{
 switch(status)
 {
  case 0:
   {
    if (sc==0xe0){status=1;break;} //extended
    onKeycode(sc);
   } 
  break;
  
  case 1: //extended
   onKeycode(E0_keycode[sc]);
   status=0;
  break;
 }//switch(status)
}

static unsigned  scancode()
{
 while(~sys_inb(0x64)&WAIT_READ){};
 return sys_inb(0x60);
}

