char kbd_get()
{
 while(1)
 {
  if (keyPutI!=keyGetI)
     {
      char ch=keyBuffer[keyGetI++];
      keyGetI&=((1<<KEY_BUFFER_SIZE_EXP)-1);
      return ch;
     }
  onScancode(scancode());   
 }   
}
